// *toggle _item on menu
const itemSpoller = document.querySelector('.menu__item[data-spollers]');
if (itemSpoller) {
    const itemSpollerValues = itemSpoller.dataset.spollers;
    const itemSpollerMediaNum = itemSpollerValues.split(',')[0];
    const itemSpollerMediaMinMax = itemSpollerValues.split(',')[1];
    const itemSpollerLink = itemSpoller.querySelector('._item__link');
    const itemSpollerContent = itemSpoller.querySelector('._item__content');
    // itemSpollerLink.setAttribute('data-class', '_item__link')
    // itemSpollerContent.setAttribute('data-class', '_item__content')
    function toggleItems(media) {
        if (media.matches && itemSpoller.classList.contains('_item')) {
            // itemSpollerLink.classList.remove('_item__link')
            itemSpollerContent.classList.remove('_item__content')
            itemSpoller.classList.remove('_item', '_change');
        } else if (!media.matches && !itemSpoller.classList.contains('_item')) {
            itemSpoller.classList.add('_item')
            // itemSpollerLink.classList.add('_item__link')
            itemSpollerLink.classList.remove('_active')
            itemSpollerContent.classList.add('_item__content')
        }
    }
    const itemMedia = window.matchMedia(`(${itemSpollerMediaMinMax}-width: ${itemSpollerMediaNum}px)`);
    itemMedia.addListener(toggleItems);
    toggleItems(itemMedia);
}
// *prx scroll
const prxScrollImages = document.querySelectorAll('._prx-sroll');
if (prxScrollImages.length > 0) {
    const body = document.body;
    const moveCoef = 0.1;
    let scrollParallax;
    function parallaxMob(media) {
        if (!media.matches) {
            body.classList.add('_parallax');
            scrollParallax = function () {
                for (let index = 0; index < prxScrollImages.length; index++) {
                    const prxScrollImage = prxScrollImages[index];
                    let r = prxScrollImage.getBoundingClientRect();
                    let parallaxYCenter = r.top + r.height / 2;
                    let scrollYCenter = window.innerHeight / 2;

                    let move = (parallaxYCenter - scrollYCenter) * moveCoef;
                    prxScrollImage.style.transform = `translate3d(0, ${move}%, 0)`;
                }
            }
            window.addEventListener("scroll", scrollParallax);
        } else if (media.matches && body.classList.contains('_parallax')) {
            body.classList.remove('_parallax');
            window.removeEventListener("scroll", scrollParallax);
            prxScrollImages.forEach(prxScrollImage => {
                prxScrollImage.style.transform = '';
            });
        }
    }
    const coinMedia = window.matchMedia('(max-width: 767.98px)');
    coinMedia.addListener(parallaxMob);
    parallaxMob(coinMedia);
}

