// *Side wrapper
let scr_fix_block = document.querySelectorAll('._side-wrapper');
if (scr_fix_block.length > 0) {
	window.addEventListener('scroll', function () {
		fix_block(scr_fix_block)
	});
	function fix_block(scr_fix_block) {
		if (!_is_hidden(scr_fix_block)) {
			let scr_value = pageYOffset;
			let window_width = parseInt(window.innerWidth);
			let window_height = parseInt(window.innerHeight);
			let header_height = parseInt(document.querySelector('.header__wrapper').offsetHeight) + 15;
			// let header_height = 15;
			for (let index = 0; index < scr_fix_block.length; index++) {
				const block = scr_fix_block[index];
				let block_width = block.getAttribute('data-width');
				const item = block.querySelector('._side-block');
				if (!block_width) { block_width = 0; }
				if (window_width > block_width) {
					if (item.offsetHeight < window_height - (header_height + 30)) {
						if (scr_value > offset(block).top - (header_height)) {
							item.style.cssText = "position:fixed;bottom:auto;top:" + header_height + "px;width:" + block.offsetWidth + "px;left:" + offset(block).left + "px;";
						} else {
							gotoRelative(item);
						}
						if (scr_value > (block.offsetHeight + offset(block).top) - (item.offsetHeight + (header_height))) {
							block.style.cssText = "position:relative;";
							item.style.cssText = "position:absolute;bottom:0px;top:auto;left:0px;width:100%";
						}
					} else {
						gotoRelative(item);
					}
				}
			}
			function gotoRelative(item) {
				item.style.cssText = "position:relative;bottom:auto;top:0px;left:0px;";
			}
		}
	}
	fix_block(scr_fix_block)
	const move = new MoveTo({
		tolerance: 0,
		duration: 0,
		easing: 'easeOutQuart'
	})
	function moveOnChange(media) {
		move.move(10);
		/* if (media.matches) {
		} else {
			move.move(-10);
		} */
	}
	const sideMedia = window.matchMedia('(min-width: 1023.98px)');
	sideMedia.addListener(moveOnChange);
}

